package com.apro.test;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = (int) 'c';
		char ch = 'a'+(char)5 ;
		System.out.println(a);
		System.out.println(ch);
		byte b = 1;
		byte d = (byte) (b+10);
		System.out.println(d);
	}

}
